import React, { useRef, useEffect, useState } from 'react';
import ChatBox from './components/ChatBox';
import InputBox, { ChatContext } from './components/InputBox';
import './styles/chatbot.css';


function App() {
  const chatBoxRef = useRef(null);
  const [messages, setMessages] = useState([
    { sender: "bot", text: "üëã Hi! I‚Äôm your health assistant. Say hi" }
  ]);
  const [loading, setLoading] = useState(false);
  const [onboardingStep, setOnboardingStep] = useState(0); // 0: wait hi, 1: wait name, 2: wait age, 3: normal
  const [userName, setUserName] = useState("");
  const [userAge, setUserAge] = useState("");

  useEffect(() => {
    if (chatBoxRef.current) {
      chatBoxRef.current.scrollTop = chatBoxRef.current.scrollHeight;
    }
  }, [messages]);

  // Custom send handler for onboarding
  const handleSend = async (input) => {
    if (!input.trim()) return;
    setMessages(prev => [...prev, { sender: "user", text: input }]);

    if (onboardingStep === 0) {
      // After hi, ask for name
      setMessages(prev => [...prev, { sender: "bot", text: "Hi what is your name?" }]);
      setOnboardingStep(1);
      return;
    }
    if (onboardingStep === 1) {
      // Save name, ask for age
      setUserName(input.trim());
      setMessages(prev => [...prev, { sender: "bot", text: `Nice to meet you, ${input.trim()}. How old are you?` }]);
      setOnboardingStep(2);
      return;
    }
    if (onboardingStep === 2) {
      // Save age, continue normal chat
      setUserAge(input.trim());
      setMessages(prev => [...prev, { sender: "bot", text: `Thank you, ${userName}. You can now tell me your medical concern.` }]);
      setOnboardingStep(3);
      return;
    }
    // Normal chat
    setLoading(true);
    try {
      // Pass userName and userAge to backend
      const reply = await import('./services/api').then(api => api.sendMessageToAPI(input, userName, userAge));
      setMessages(prev => [...prev, { sender: "bot", text: reply }]);
    } catch {
      setMessages(prev => [...prev, { sender: "bot", text: "‚ö†Ô∏è Server error!" }]);
    }
    setLoading(false);
  };

  return (
    <ChatContext.Provider value={{ messages, setMessages, loading, setLoading, handleSend }}>
      <div className="chat-container">
        <div className="chat-header">ü§ñ Health Chatbot</div>
        <ChatBox chatBoxRef={chatBoxRef} />
        <InputBox />
      </div>
    </ChatContext.Provider>
  );
}

export default App;
