import React, { useState } from 'react';
import ChatBox from '../components/ChatBox';
import InputBox from '../components/InputBox';
import { sendMessageToAPI } from '../services/api';

const ChatPage = () => {
  const [messages, setMessages] = useState([
    { sender: "bot", text: "Hello ðŸ‘‹ I am your medical assistant. What is your name and age?" }
  ]);
  const [name, setName] = useState("");
  const [age, setAge] = useState("");
  const [collectingInfo, setCollectingInfo] = useState(true);

  const handleInfoSubmit = (e) => {
    e.preventDefault();
    if (name.trim() && age.trim()) {
      setMessages(prev => [...prev, { sender: "user", text: `My name is ${name} and my age is ${age}` }]);
      setCollectingInfo(false);
    }
  };

  const handleSend = async (text) => {
    const newMessage = { sender: "user", text };
    setMessages(prev => [...prev, newMessage]);

    const botResponse = await sendMessageToAPI(text, name, age);
    setMessages(prev => [...prev, { sender: "bot", text: botResponse }]);
  };

  return (
    <div className="chat-container">
      <ChatBox messages={messages} />
      {collectingInfo ? (
        <form className="user-info-form" onSubmit={handleInfoSubmit} style={{ display: 'flex', gap: 8, marginTop: 8 }}>
          <input
            type="text"
            placeholder="Enter your name"
            value={name}
            onChange={e => setName(e.target.value)}
            required
          />
          <input
            type="number"
            placeholder="Enter your age"
            value={age}
            onChange={e => setAge(e.target.value)}
            required
            min="1"
          />
          <button type="submit">Start Chat</button>
        </form>
      ) : (
        <InputBox onSend={handleSend} />
      )}
    </div>
  );
};

export default ChatPage;
