import React, { useState, createContext } from 'react';
import { sendMessageToAPI } from '../services/api';

export const ChatContext = createContext();

const InputBox = () => {
	const [input, setInput] = useState("");
	const [messages, setMessages] = useState([
		{ sender: "bot", text: "ðŸ‘‹ Hi! Iâ€™m your health assistant. Say hi or tell me your symptoms." }
	]);
	const [loading, setLoading] = useState(false);

	const handleSend = async () => {
		if (!input.trim()) return;
		setMessages(prev => [...prev, { sender: "user", text: input }]);
		setLoading(true);
		setInput("");
		try {
			const reply = await sendMessageToAPI(input);
			setMessages(prev => [...prev, { sender: "bot", text: reply }]);
		} catch {
			setMessages(prev => [...prev, { sender: "bot", text: "âš ï¸ Server error!" }]);
		}
		setLoading(false);
	};

	const handleKeyPress = (e) => {
		if (e.key === "Enter") handleSend();
	};

	return (
		<ChatContext.Provider value={{ messages }}>
			<div className="input-area">
				<input
					type="text"
					placeholder="Type your message..."
					value={input}
					onChange={e => setInput(e.target.value)}
					onKeyPress={handleKeyPress}
					disabled={loading}
				/>
				<button onClick={handleSend} disabled={loading}>âž¤</button>
			</div>
		</ChatContext.Provider>
	);
};

export default InputBox;
